import { Component, Renderer2, RendererFactory2 } from '@angular/core';
import { SharedService } from '../shared.service';
import { AppointmentsService } from '../appointment.service';
import { Appointment  } from "../appointment.model";


@Component({
  selector: 'app-calendar',
  templateUrl: './calendar.component.html',
  styleUrls: ['./calendar.component.css']
})

export class CalendarComponent {
[x: string]: any;
  selectedDate: Date = new Date();
  private renderer: Renderer2;

  constructor(private rendererFactory: RendererFactory2, public sharedService: SharedService, private appointmentsService: AppointmentsService) {
    this.renderer = rendererFactory.createRenderer(null, null);
  }

    // === Appointment form condition === //
    showAppointmentForm = this.appointmentsService.showAppointmentForm;

    appointmentDate: string | null = null;
    selectedAppointment: Appointment | null = null;


    onAddAppointment(appointment: Appointment) {
      if (!appointment.title) {
        appointment.title = 'Unknown Title'; 
      }
      if (this.appointmentDate) {
        appointment.date = this.appointmentDate;
        this.selectedAppointment = appointment;
      }
      this.showAppointmentForm = false;
    }

    onCloseForm() {
      this.showAppointmentForm = false;
    }

  // ----------- DAY CHANGE -------------//
  dateChanged(event: any) {
    console.log("Date changed", event);
  }

  // ----------- MONTH CHANGE -------------//
  ngAfterViewInit() {
    const monthPrevBtn = document.querySelectorAll(
      '.mat-calendar-previous-button'
    );
    const monthNextBtn = document.querySelectorAll('.mat-calendar-next-button');

    if (monthPrevBtn) {
      Array.from(monthPrevBtn).forEach((button) => {
        this.renderer.listen(button, 'click', (event: any) => {
        });
      });
    }

    if (monthNextBtn) {
      Array.from(monthNextBtn).forEach((button) => {
        this.renderer.listen(button, 'click', (event: any) => {
        });
      });
    }
  }

  handleMonthSelected(event: any) {
    console.log("Month changed", event);
  }

  // ---------YEAR CHANGE ----------//

  handleYearSelected(event: any) {
    console.log("Year changed", event);
}



}
<div class="relative">
  <button (click)="showAppointmentForm = true; " class="block mx-auto px-12 py-5 border text-base font-medium bg-purple text-white hover:bg-pale-purple hover:text-inherit rounded-full shadow-2xl">Creat</button>
  <div class="flex flex-col items-center px-3">
    <mat-calendar class="w-full" [(selected)]="selectedDate"  (selectedChange)="dateChanged($event)" (monthSelected)="handleMonthSelected($event)" (yearSelected)="handleYearSelected($event)"></mat-calendar>
  </div>
  <app-appointment-form  *ngIf="showAppointmentForm" (addAppointment)="onAddAppointment($event)" (closeForm)="onCloseForm()" class="mx-5 py-3"></app-appointment-form>
  <p class="text-center py-9 text-fuchsia-300 font-medium text-lg">Double click on table to submit an appointment</p>
</div>
