import { Component } from '@angular/core';
import { SharedService } from '../shared.service';
import { Router } from '@angular/router'

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {

  selectedOption: string = 'day'; 

  constructor(private sharedService: SharedService, private router: Router) {}

  currentMonth = this.sharedService.currentMonth;

  callPreviousMonthFromHeader() {
    this.sharedService.previousMonth();
  }
  callNextMonthFromHeader() {
    this.sharedService.nextMonth();
  }
  callSwitchToMonthFromHeader() {
    this.sharedService.switchToMonthView();
  }
  callSwitchToDayFromHeader() {
    this.sharedService.switchToDayView();
  }
  callSwitchToYearFromHeader() {
    this.sharedService.switchToYearView();
  }
  callSwitchToWeekFromHeader() {
    this.sharedService.switchToWeekView();
  }


  onSelectChange(){

    if (this.selectedOption === 'day') {
      this.callSwitchToDayFromHeader();
      this.router.navigate([this.selectedOption])
      console.log('day')
    } else if (this.selectedOption === 'week') {
      this.callSwitchToWeekFromHeader();
      this.router.navigate([this.selectedOption])
      console.log('week')
    } else if (this.selectedOption === 'month') {
      this.callSwitchToMonthFromHeader();
      this.router.navigate([this.selectedOption])
      console.log('month')
    } else if (this.selectedOption === 'year') {
      this.callSwitchToYearFromHeader();
      this.router.navigate([this.selectedOption])
      console.log('year')
    } 

  }


}
<div class="flex justify-between items-center py-5 px-12 bg-pale-purple">
    <div class="flex items-center justify-between gap-6 mt-4">
        <h2 class="text-base font-bold dark:text-gray-100 text-gray-800 " >{{ currentMonth | date: 'MMMM y' }}</h2>
        <div class="mb-4">
          <button (click)="callPreviousMonthFromHeader()" class="mx-2">
            <i class="fa-solid fa-angle-left" style="color: #636e72;"></i>
          </button>
          <button (click)="callNextMonthFromHeader()" class="mx-2">
            <i class="fa-solid fa-angle-right" style="color: #636e72;"></i>
          </button>
        </div>
    </div>
    <p class="text-2xl font-bold text-center my-10">Calender</p>
    <div class="relative inline-flex">
      <svg class="w-2 h-2 absolute top-0 right-0 m-4 pointer-events-none" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 412 232"><path d="M206 171.144L42.678 7.822c-9.763-9.763-25.592-9.763-35.355 0-9.763 9.764-9.763 25.592 0 35.355l181 181c4.88 4.882 11.279 7.323 17.677 7.323s12.796-2.441 17.678-7.322l181-181c9.763-9.764 9.763-25.592 0-35.355-9.763-9.763-25.592-9.763-35.355 0L206 171.144z" fill="#648299" fill-rule="nonzero"/></svg>
      <select [(ngModel)]="selectedOption" (ngModelChange)="onSelectChange()" class="border border-gray-300 rounded-full text-gray-600 h-10 pl-5 pr-10 bg-white hover:border-gray-400 focus:outline-none appearance-none">
        <option value="day" [selected]="selectedOption === 'day'">Day</option>
        <option value="week" [selected]="selectedOption === 'week'">Week</option>
        <option value="month" [selected]="selectedOption === 'month'">Month</option>
        <option value="year" [selected]="selectedOption === 'year'">Year</option>
      </select>
    </div>

</div>
