import { Component } from '@angular/core';
import { CdkDragDrop, CdkDragStart, CdkDragEnd } from '@angular/cdk/drag-drop';
import { SharedService } from '../shared.service';
import { AppointmentsService } from '../appointment.service';
import { Appointment  } from "../appointment.model";


@Component({
  selector: 'month-view-calender',
  templateUrl: './month-view-calender.component.html',
  styleUrls: ['./month-view-calender.component.css']
})

export class  MonthViewCalenderComponent {
  [x: string]: any; 

  constructor(
    public sharedService: SharedService, 
    private appointmentsService: AppointmentsService
  ) {};

  daysOfWeek = this.sharedService.daysOfWeek;
  weeks = this.sharedService.weeks

  // === Appointment form condition === //

  showAppointmentForm = this.appointmentsService.showAppointmentForm;
  openAppointmentForm(day: string) {
    this.selectedDate = day;
    this.selectedAppointment = {
      title: '',
      date: this.selectedDate || '',
      startTime: new Date(),
      endTime: new Date(),
    };
    this.showAppointmentForm = true;
  }

  selectedDate: string | null = null;
  selectedAppointment: Appointment | null = null;


  onAddAppointment(appointment: Appointment) {
    if (!appointment.title) {
      appointment.title = 'Unknown Title'; 
    }
    if (this.selectedDate) {
      appointment.date = this.selectedDate;
      this.selectedAppointment = appointment;
    }
    this.showAppointmentForm = false;
  }
  onDeleteAppointment() {
    if (this.selectedAppointment) {
      if (this.selectedAppointment.date) { 
        const selectedAppointmentTimestamp = new Date(this.selectedAppointment.date).getTime();

        const index = this.appointmentsService.appointments.findIndex(
          (appointment) => {
            if (appointment.date) {
              const appointmentTimestamp = new Date(appointment.date).getTime();
              return appointmentTimestamp === selectedAppointmentTimestamp &&
                appointment.startTime === this.selectedAppointment?.startTime && 
                appointment.endTime === this.selectedAppointment?.endTime; 
            }
            return false; 
          }
        );

        if (index !== -1) {
          this.appointmentsService.appointments.splice(index, 1);
        }
      }

      this.selectedAppointment = null;
    }
  }

  onCloseForm() {
    this.showAppointmentForm = false;
  }

  // === Drag & Drop === //
  // setSelectedDay(day: string) {
  //   this.selectedDate = day;
  // }

  onDragStarted(event: CdkDragStart, appointment: Appointment) {
    this.selectedAppointment = appointment;
  }

  onDrop(event: CdkDragDrop<string[]> ) {
    const newDate = event.container.data[event.currentIndex];

    if (this.selectedAppointment) {
      this.selectedAppointment.date = newDate;
      this.selectedAppointment = null;
    }
  }


}
<div class="flex items-center pt-3 mx-auto" class="relative">
  <table class="border w-full">
    <thead class="border">
      <tr>
        <th *ngFor="let day of daysOfWeek">
          <p class="pt-3 flex justify-center items-center text-base font-medium  text-pale-gray dark:text-gray-100">{{ day }}</p>
        </th>
      </tr>
    </thead>
    <tbody >
      <tr class="border"  *ngFor="let week of weeks; let i = index">
        <td class="pt-1 border" *ngFor="let day of week">
          <div (dblclick)="openAppointmentForm(day.day)" class="py-16 px-2 cursor-pointer flex justify-center items-start">{{ day.day }}</div>
          <div *ngIf="selectedAppointment && selectedAppointment.date === day.day" 
          cdkDrag   (cdkDragStarted)="onDragStarted($event, selectedAppointment)"
          (cdkDragEnded)="onDrop($event)">
            <div class="text-center">
              <div class="bg-fuchsia-300 relative" >
                <span (click)="onDeleteAppointment()" class="absolute top-0 end-0 pe-1 cursor-pointer ">
                  <i  class="fa-solid fa-xmark" style="color: #000000;"></i>
                </span>
                <p>{{ selectedAppointment.title }}</p>
                <p >{{ day.day }} - {{ selectedAppointment.startTime | date:'HH:mm' }} - {{ selectedAppointment.endTime | date:'HH:mm' }}</p>
              </div>
            </div>
          </div>
        </td>
      </tr>
    </tbody>
  </table>
  <app-appointment-form *ngIf="showAppointmentForm" [selectedDate]="selectedDate" (addAppointment)="onAddAppointment($event)" (closeForm)="onCloseForm()" class="absolute top-1 left-[50%] -translate-x-[50%] w-[35rem]"></app-appointment-form>
</div>
